import React from 'react';
import { useContext, useState } from 'react';
import Modal from '../UI/Modal';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';
import Checkout from './Checkout';

const Cart = (props) => {
	const [isCheckout, setIsCheckout] = useState(false);

	const [isSubmiting, setIsSubmiting] = useState(false);
	const [didSubmit, setDidSubmit] = useState(true);

	const cartCtx = useContext(CartContext);

	const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;

	const hasItems = cartCtx.items.length > 0;

	const cartItemRemoveHandler = (id) => {
		cartCtx.removeItem(id);
	};

	const cartItemAddHandler = (item) => {
		cartCtx.addItem({ ...item, amount: 1 });
	};

	const orderHandler = () => {
		setIsCheckout(true);
	};

	const submitOrderHandler = async (userData) => {
		setIsSubmiting(true);
		const response = await fetch(
			'https://react-http-bbcab-default-rtdb.firebaseio.com/orders.json',
			{
				method: 'POST',
				body: JSON.stringify({
					user: userData,
					orderedItems: cartCtx.items,
				}),
			}
		);
		setDidSubmit(true);
		setIsSubmiting(false);
	};

	const cartItems = (
		<ul className={classes['cart-items']}>
			{cartCtx.items.map((item) => (
				<CartItem
					key={item.id}
					name={item.name}
					amount={item.amount}
					price={item.price}
					onRemove={cartItemRemoveHandler.bind(null, item.id)}
					onAdd={cartItemAddHandler.bind(null, item)}
				/>
			))}
		</ul>
	);

	const modalActions = (
		<div className={classes.actions}>
			<button className={classes['button--alt']} onClick={props.onClose}>
				Close
			</button>
			{hasItems && (
				<button className={classes.button} onClick={orderHandler}>
					Order
				</button>
			)}
		</div>
	);

	const cartModalContent = (
		<React.Fragment>
			{cartItems}
			<div className={classes.tottal}>
				<span>Tottal Ammount</span>
				<span>{totalAmount}</span>
			</div>
			{isCheckout && (
				<Checkout
					onConfirm={submitOrderHandler}
					onCancel={props.onClose}
				></Checkout>
			)}
			{!isCheckout && modalActions}
		</React.Fragment>
	);


		const isSubmitingModalContent = <p>Sending order data ...</p>


		const didSubmitModalContent = <p> Succesfully sent the order !</p>;

	return <Modal onClose={props.onClose}>
		{!isSubmiting && !didSubmit  && cartModalContent}
		{isSubmiting && isSubmitingModalContent}
		{!isSubmiting && didSubmit && didSubmitModalContent}
	</Modal>;
};

export default Cart;
