import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';
import { useRef, useState } from 'react';




const MealItemForm = (props) => {
  

  const[ammountIsValid, setAmmountIsValid]=useState (true)

  const ammountInputRef = useRef ();
  
  const submitHandler = event => {
    event.preventDefauld();

    const enteredAmmount = ammountInputRef.current.value;
    const enteredAmmountNumber = +enteredAmmount;


    if(enteredAmmount.trim().length === 0 || enteredAmmountNumber <1 || enteredAmmountNumber >5){
      setAmmountIsValid(false);
      return;
    }
  };

return (
<form className={classes.form} onSubmit={submitHandler}>
  <Input 
  ref={ammountInputRef}
  label="Ammount" 
  input={{
    
    id:'ammount_'+props.id,
    type: 'number',
    min:'1',
    max:'10',
    step:'1',
    default:'1'
  }}></Input>
    <button>Add To Cart</button>
</form>

);
};

export default MealItemForm;
